(function($) {
  "use strict"; // Start of use strict

  // Smooth scrolling using jQuery easing
  var isAbletoScroll;
  $('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function() {
	if(isAbletoScroll){
		if (location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '') && location.hostname == this.hostname) {
	      var target = $(this.hash);
	      target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');
	      if (target.length) {
	        $('html, body').animate({
	          scrollTop: (target.offset().top)
	        }, 1000, "easeInOutExpo");
	        return false;
	      }
	    }
	}
    
  });

  // Closes responsive menu when a scroll trigger link is clicked
  $('.js-scroll-trigger').click(function() {
    $('.navbar-collapse').collapse('hide');
  });

  // Activate scrollspy to add active class to navbar items on scroll
  $('body').scrollspy({
    target: '#sideNav'
  });

  $(".galleryImage").click(function(){
  	$(".modala").removeClass('hidden');
  	$(".model-image").attr('src', $(this).attr('src'));
  	if($(this).attr('src') == "img/gallery/2.jpg"){
  		$(".model-image").addClass("randomRotatationNeeded");
  	}
  	isAbletoScroll = false;
  	
  });
  $(".close").click(function(){
  	$(".modala").addClass('hidden');
  	isAbletoScroll=true;
  	$(".model-image").removeClass("randomRotatationNeeded");
  });
	
var d = new Date();
var n = d.getMonth();


$("#CurrentDate").html(n);	
	
	
})(jQuery); // End of use strict
